(18, 'document', 'text/html', 'Регистрация', '', '', 'register', 1, '', 1, 0, 0, 28, 0, '', '[[!Register?\r\n  &submitVar=`login-register-btn`\r\n  &activation=`1`\r\n  &activationEmailSubject=`Подтверждение регистрации`\r\n  &activationResourceId=`19`\r\n  &successMsg=`Спасибо за регистрацию. На вашу электронную почту <span class=\"font-weight-bold\">[[!+reg.email]]</span> отправлено письмо, содержащее ссылку, необходимую для активации аккаунта. Перейдите по ссылке в письме, чтобы завершить процедуру регистрации.`\r\n  &usergroups=`Users`\r\n  &usernameField=`email`\r\n  &passwordField=`password`\r\n  &validate=`nospam:blank,\r\n    password:required:minLength=^8^,\r\n    password_confirm:password_confirm=^password^,\r\n    fullname:required,\r\n    email:required:email`\r\n  &placeholderPrefix=`reg.`\r\n]]\r\n<div class=\"row\">\r\n          <div class=\"col-sm-3 text-center pl-4\"><i class=\"fas fa-user-astronaut fa-8x pb-4\"></i></div>\r\n          <div class=\"col-sm-6\">\r\n    <div class=\"card-body\">\r\n      [[!+error.message:eq=``:then=`\r\n        <form class=\"form\" action=\"[[~[[*id]]]]\" method=\"post\">\r\n        <input type=\"hidden\" name=\"nospam\" id=\"nospam\" value=\"[[!+reg.nospam]]\" />\r\n        <div class=\"form-group\">\r\n          <label for=\"fullname\">Имя:</label>\r\n          <input type=\"text\" name=\"fullname\" class=\"form-control\" value=\"[[!+reg.fullname]]\">\r\n          [[!+reg.error.fullname]]\r\n        </div>\r\n        <div class=\"form-group\">\r\n          <label for=\"email\">Email:</label>\r\n          <input type=\"email\" name=\"email\" class=\"form-control\" value=\"[[!+reg.email]]\">\r\n          [[!+reg.error.email]]\r\n        </div>\r\n        <div class=\"form-group\">\r\n          <label for=\"password\">Пароль:</label>\r\n          <input type=\"password\" name=\"password\" class=\"form-control\" value=\"[[!+reg.password]]\">\r\n          [[!+reg.error.password]]\r\n        </div>\r\n        <div class=\"form-group\">\r\n          <label for=\"password_confirm\">Введите пароль ещё раз:</label>\r\n          <input type=\"password\" name=\"password_confirm\" class=\"form-control\" value=\"[[!+reg.password_confirm]]\">\r\n          [[!+reg.error.password_confirm]]\r\n        </div>\r\n        <input type=\"submit\" name=\"login-register-btn\" class=\"btn btn-primary\" value=\"Отправить\">\r\n      </form>\r\n      `:else=`[[!+error.message]]`]]\r\n</div>\r\n</div>\r\n</div>\r\n', 0, 4, 6, 0, 0, 1, 1607837951, 1, 1608020049, 0, 0, 0, 1607838000, 1, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'register.html', 0, 0, 1, NULL),
