(20, 'document', 'text/html', 'Авторизация', '', '', 'authlogin', 1, '', 1, 0, 0, 28, 0, '', '[[!Login? \r\n  &tplType=`modChunk`\r\n  &loginTpl=`myLoginChunk`\r\n  &logoutTpl=`myLogoutChunk`\r\n  &errTpl=`lgnErrTpl` \r\n  &redirectToPrior=`1`\r\n]]\r\n\r\n\r\n      <div class=\"row\">\r\n          <div class=\"col-sm-3 text-center pl-4\"><i class=\"fas fa-user-astronaut fa-8x pb-4\"></i></div>\r\n          <div class=\"col-sm-6\">\r\n        \r\n        <div class=\"panel-body\">\r\n          <p class=\"text-danger\">[[+errors]]</p>\r\n          <form class=\"form-horizontal\" action=\"[[~[[*id]]]]\" method=\"post\">\r\n              <label for=\"username\" class=\"control-label ml-4\">Имя пользователя</label>\r\n                <div class=\"input-group col-sm pb-4\">\r\n                    <div class=\"input-group-prepend\">\r\n                      <div class=\"input-group-text\" id=\"btnGroupAddon2\"><i class=\"fas fa-user-astronaut\"></i></div>\r\n                    </div>\r\n                    <input type=\"text\" name=\"username\" class=\"form-control\" id=\"username\">\r\n                </div>\r\n              <label for=\"password\" class=\"control-label ml-4\">Пароль</label>\r\n\r\n                <div class=\"input-group col-sm pb-4\">\r\n                    <div class=\"input-group-prepend\">\r\n                      <div class=\"input-group-text\" id=\"btnGroupAddon2\"><i class=\"fas fa-unlock-alt\"></i></div>\r\n                    </div>\r\n                <input type=\"password\" name=\"password\" class=\"form-control\" id=\"password\">\r\n                </div>\r\n      \r\n            <input class=\"returnUrl\" type=\"hidden\" name=\"returnUrl\" value=\"[[+request_uri]]\" />\r\n            <input class=\"loginLoginValue\" type=\"hidden\" name=\"service\" value=\"login\">\r\n            <div class=\" text-center\">\r\n                <input type=\"submit\" value=\"[[+actionMsg]]\" name=\"Login\" id=\"Login\" class=\"btn btn-primary btn-block\">\r\n            </div>\r\n          </form>\r\n          <div class=\"clearfix\"></div>\r\n          <hr style=\"margin-top:10x;margin-bottom:50px;\">\r\n          <p><a href=\"[[~21]]\" class=\"btn btn-secondary btn-block\">Забыли пароль?</a>&nbsp;<a href=\"[[~18]]\" class=\"btn btn-secondary btn-block\">Регистрация</a></p>\r\n        </div>\r\n      </div>\r\n      </div>\r\n', 0, 4, 4, 0, 0, 1, 1607838006, 1, 1608020032, 0, 0, 0, 1607838000, 1, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'authlogin.html', 0, 0, 1, NULL),
(21, 'document', 'text/html', 'Восстановление пароля', '', '', 'pwdrecovery', 1, '', 1, 0, 0, 28, 0, '', '[[!ResetPassword:empty=`\r\n  [[!ForgotPassword?  \r\n    &resetResourceId=`[[*id]]`\r\n    &loginResourceId=`20`\r\n    &tpl=`lgnForgotPassTpl`\r\n    &sentTpl=`lgnForgotPassSentTpl`\r\n    &emailTpl=`lgnForgotPassEmail`\r\n    &emailSubject=`Восстановление пароля`\r\n  ]]`? &tpl=`lgnResetPassTpl`\r\n       &expiredTpl=`lgnExpiredTpl`\r\n       &loginResourceId=`20`\r\n]]', 0, 4, 3, 0, 0, 1, 1607838049, 1, 1607946670, 0, 0, 0, 1607838000, 1, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'pwdrecovery.html', 0, 0, 1, NULL),
(22, 'document', 'text/html', 'Личный кабинет', '', '', 'userpage', 1, '', 1, 0, 0, 28, 0, '', '[[!Profile? &prefix=`usr.`]]\r\n\r\n<div class=\"row\">\r\n          <div class=\"col-sm-3 text-center pl-4\"><i class=\"fas fa-user-astronaut fa-8x pb-4\"></i></div>\r\n          <div class=\"col-sm-6\">\r\n    <div class=\"panel-body\">\r\n      <p><strong>Имя пользователя:</strong> [[+usr.username]]</p>\r\n      <p><strong>Телефон:</strong> [[+usr.phone]]</p>\r\n      <p><strong>Мобильный телефон:</strong> [[+usr.mobilephone]]</p>\r\n      <p><strong>Email:</strong> [[+usr.email]]</p>\r\n      <hr>\r\n      <p><strong>Адрес:</strong> [[+usr.address]]</p>\r\n      <p><strong>Страна:</strong> [[+usr.country]]</p>\r\n      <p><strong>Город:</strong> [[+usr.city]]</p>\r\n      <p><strong>Веб-сайт:</strong> [[+usr.website]]</p>\r\n      <hr>\r\n      <div class=\"col-xs-6\" style=\"margin-bottom:8px;\">\r\n       <i class=\"glyphicon glyphicon-pencil\"></i> <a href=\"[[~23]]\" class=\"btn btn-secondary btn-block\" title=\"Изменение пароля\">Изменение пароля</a>\r\n      </div>\r\n      <div class=\"col-xs-6\" style=\"margin-bottom:8px;\">\r\n        <i class=\"glyphicon glyphicon-cog\"></i> <a href=\"[[~24]]\"  class=\"btn btn-secondary btn-block\" title=\"Редактирование данных\">Редактирование данных</a>\r\n      </div>\r\n    </div>\r\n</div>\r\n</div>\r\n', 0, 4, 2, 0, 0, 1, 1607838087, 1, 1608020017, 0, 0, 0, 1607838060, 1, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'userpage.html', 0, 0, 1, NULL),
(23, 'document', 'text/html', 'Изменение пароля', '', '', 'pwdchange', 1, '', 1, 0, 0, 28, 0, '', '[[!ChangePassword?\r\n   &submitVar=`change-password`\r\n   &placeholderPrefix=`cp.`\r\n   &validateOldPassword=`1`\r\n   &validate=`nospam:blank`\r\n   &reloadOnSuccess=`0`\r\n   &successMessage=`Ваш пароль успешно изменён`\r\n]]\r\n\r\n<div class=\"row\">\r\n          <div class=\"col-sm-3 text-center pl-4\"><i class=\"fas fa-user-astronaut fa-8x pb-4\"></i></div>\r\n          <div class=\"col-sm-6\">\r\n            <div class=\"panel-body\">\r\n          <div class=\"updprof-error\">[[!+cp.error_message]]</div>\r\n          <p>[[!+cp.successMessage]]</p>\r\n          <form class=\"form-horizontal\" action=\"[[~[[*id]]]]\" method=\"post\">\r\n            <input type=\"hidden\" name=\"nospam\" value=\"\">\r\n            <div class=\"form-group\">\r\n              <label for=\"password_old\" class=\" control-label\"><strong>Старый пароль</strong></label>\r\n              <div   >\r\n                <input type=\"password\" name=\"password_old\" id=\"password_old\" value=\"[[+cp.password_old]]\" class=\"form-control\">\r\n                <span class=\"help-block text-error\">\r\n                  [[!+cp.error.password_old]]\r\n                </span>   \r\n              </div>      \r\n            </div>\r\n            <div class=\"form-group\">\r\n              <label for=\"password_new\" class=\" control-label\"><strong>Новый пароль</strong></label>\r\n              <div   >\r\n                <input type=\"password\" name=\"password_new\" id=\"password_new\" value=\"[[+cp.password_new]]\" class=\"form-control\">\r\n                <span class=\"help-block text-error\">\r\n                  [[!+cp.error.password_new]]\r\n                </span>  \r\n              </div>      \r\n            </div>   \r\n            <div class=\"form-group\">\r\n              <label for=\"password_new_confirm\" class=\" control-label\"><strong>Введите новый пароль ещё раз</strong></label>\r\n              <div   >\r\n                <input type=\"password\" name=\"password_new_confirm\" id=\"password_new_confirm\" value=\"[[+cp.password_new_confirm]]\" class=\"form-control\">\r\n                <span class=\"help-block text-error\">\r\n                  [[!+cp.error.password_new_confirm]]\r\n                </span>  \r\n              </div>      \r\n            </div>\r\n            <div class=\"text-center\">\r\n            <input type=\"submit\" value=\"Изменить пароль\" name=\"change-password\" id=\"change-password\" class=\"btn btn-primary btn-block\">\r\n            </div>\r\n          </form>\r\n        </div>\r\n      </div>\r\n</div>', 0, 4, 1, 0, 0, 1, 1607838109, 1, 1608019973, 0, 0, 0, 1607838060, 1, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'pwdchange.html', 0, 0, 1, NULL),
(24, 'document', 'text/html', 'Редактирование данных', '', '', 'useredit', 1, '', 1, 0, 0, 28, 0, '', '[[!UpdateProfile? &validate=`fullname:required`]]\r\n\r\n\r\n\r\n      <div class=\"row\">\r\n          <div class=\"col-sm-3 text-center pl-4\"><i class=\"fas fa-user-astronaut fa-8x pb-4\"></i></div>\r\n          <div class=\"col-sm-6\">\r\n        <div class=\"panel-heading\"></div>\r\n        <div class=\"panel-body\">\r\n          <div class=\"updprof-error\">[[+error.message]]</div>\r\n          [[+login.update_success:is=`1`:then=`[[%login.profile_updated? &namespace=`login` &topic=`updateprofile`]]`]]\r\n          <form action=\"[[~[[*id]]]]\" method=\"post\" class=\"form-horizontal\">\r\n            <input type=\"hidden\" name=\"nospam\" value=\"\">\r\n            <div class=\"form-group\">\r\n              <label for=\"fullname\" class=\"control-label\"><strong>[[!%login.fullname? &namespace=`login` &topic=`updateprofile`]]</strong></label>\r\n              <div>\r\n                <input type=\"text\" name=\"fullname\" class=\"form-control\" id=\"fullname\" value=\"[[+fullname]]\">\r\n                <span class=\"help-block text-error\">\r\n                  [[+error.fullname]]\r\n                </span>        \r\n              </div>      \r\n            </div>\r\n            <div class=\"form-group\">\r\n              <label for=\"phone\" class=\"control-label\"><strong>[[!%login.phone]]</strong></label>\r\n              <div>\r\n                <input type=\"text\" name=\"phone\" class=\"form-control\" id=\"phone\" value=\"[[+phone]]\">\r\n                <span class=\"help-block text-error\">\r\n                  [[+error.phone]]\r\n                </span>        \r\n              </div>      \r\n            </div> \r\n            <div class=\"form-group\">\r\n              <label for=\"mobilephone\" class=\"control-label\"><strong>[[!%login.mobilephone]]</strong></label>\r\n              <div>\r\n                <input type=\"text\" name=\"mobilephone\" class=\"form-control\" id=\"mobilephone\" value=\"[[+mobilephone]]\">\r\n                <span class=\"help-block text-error\">\r\n                  [[+error.mobilephone]]\r\n                </span>        \r\n              </div>      \r\n            </div> \r\n            <div class=\"form-group\">\r\n              <label for=\"address\" class=\"control-label\"><strong>[[!%login.address]]</strong></label>\r\n              <div >\r\n                <input type=\"text\" name=\"address\" class=\"form-control\" id=\"address\" value=\"[[+address]]\">\r\n                <span class=\"help-block text-error\">\r\n                  [[+error.address]]\r\n                </span>        \r\n              </div>      \r\n            </div>  \r\n            <div class=\"form-group\">\r\n              <label for=\"country\" class=\"control-label\"><strong>[[!%login.country]]</strong></label>\r\n              <div >\r\n                <input type=\"text\" name=\"country\" class=\"form-control\" id=\"country\" value=\"[[+country]]\">\r\n                <span class=\"help-block text-error\">\r\n                  [[+error.country]]\r\n                </span>        \r\n              </div>      \r\n            </div>   \r\n            <div class=\"form-group\">\r\n              <label for=\"city\" class=\"control-label\"><strong>[[!%login.city]]</strong></label>\r\n              <div >\r\n                <input type=\"text\" name=\"city\" class=\"form-control\" id=\"city\" value=\"[[+city]]\">\r\n                <span class=\"help-block text-error\">\r\n                  [[+error.city]]\r\n                </span>        \r\n              </div>      \r\n            </div>  \r\n            <div class=\"form-group\">\r\n              <label for=\"website\" class=\"control-label\"><strong>[[!%login.website]]</strong></label>\r\n              <div >\r\n                <input type=\"text\" name=\"website\" class=\"form-control\" id=\"website\" value=\"[[+website]]\">\r\n                <span class=\"help-block text-error\">\r\n                  [[+error.website]]\r\n                </span>        \r\n              </div>      \r\n            </div>  \r\n            <div class=\"clearfix\"></div>\r\n            <div class=\"text-center\">\r\n                <input type=\"submit\" value=\"[[!%login.update_profile]]\" name=\"login-updprof-btn\" id=\"login-updprof-btn\" class=\"btn btn-primary btn-block\">\r\n            </div>\r\n          </form>\r\n        </div>\r\n        </div>\r\n', 0, 4, 0, 0, 0, 1, 1607838131, 1, 1608019989, 0, 0, 0, 1607838120, 1, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'useredit.html', 0, 0, 1, NULL),
