(30, 'document', 'text/html', 'test1', '', '', 'test1', 1, '', 1, 0, 0, 0, 0, '', '<form action=\"upload.php\" method=\"post\" enctype=\"multipart/form-data\">\r\n  <input type=\"file\" name=\"image\" required>\r\n  <button type=\"submit\">Загрузить</button>\r\n</form>', 0, 4, 20, 0, 0, 1, 1616573415, 1, 1616749791, 0, 0, 0, 1616573400, 1, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'test1.html', 0, 0, 1, NULL),
(31, 'document', 'text/html', 'Справочник изображений', '', '', 'images', 1, '', 1, 0, 0, 0, 0, '', '\r\n<form method=\"post\" enctype=\"multipart/form-data\">\r\n  <div class=\"row pb-4\">\r\n      <div class=\"col-sm\">\r\n      <input class=\"btn btn-primary form-control form-control-file\" type=\"file\" name=\"image\" required>\r\n      </div>\r\n      <div class=\"col-sm\">\r\n      <button class=\"form-control\" type=\"submit\">Загрузить</button>\r\n      </div>\r\n  </div>\r\n</form>\r\n[[upload]]', 0, 2, 21, 1, 0, 1, 1616999922, 1, 1617105953, 0, 0, 0, 1616999880, 1, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'images.html', 0, 0, 1, NULL);
