(30, 0, 0, 'recaptchav2_invisible_html', '', 0, 6, 0, '<script type=\"text/javascript\" src=\"https://www.google.com/recaptcha/api.js?hl=[[++cultureKey]]\"></script>\n<script>function recaptchaV2SubmitForm(response){return new Promise(function(){document.getElementById(\'[[+form_id]]\').submit();})}</script>\n<button type=\"submit\" class=\"g-recaptcha\" name=\"login\" data-sitekey=\"[[+site_key]]\" data-callback=\"recaptchaV2SubmitForm\">Login</button>', 0, 'a:0:{}', 0, ''),
(31, 0, 0, 'sample_formit_contact_form', '', 0, 6, 0, '[[!FormIt?\n   &hooks=`recaptchav2,email`\n   &emailTpl=`sample_formit_contact_eml`\n   &emailTo=`[[++emailsender]]`\n   &emailSubject=`Contact from website`\n   &validate=`name:required,\n      email:email:required,\n      text:stripTags`\n   &successMessage=`<p class=\"button success\">Success! Your enquiry has been sent.</p>`\n]]\n    <div class=\"sample_formit_contact_form\">\n        <h3>Contact Us</h3>\n        [[!+fi.validation_error_message:notempty=`<div class=\"label alert\">[[!+fi.validation_error_message]]</div>`]]\n        [[!+fi.successMessage]]\n        <form action=\"[[~[[*id]]]]\" method=\"post\" class=\"contact-form\">\n            <div class=\"form-item\">\n                <label for=\"name\">\n                    Name: *\n                    [[!+fi.error.name:notempty=`<span class=\"error\">[[!+fi.error.name]]</span>`]]\n                </label>\n                <input type=\"text\" name=\"name\" id=\"name\" value=\"[[!+fi.name]]\" />\n            </div>\n            <div class=\"form-item\">\n                <label for=\"email\">\n                    Email: *\n                    [[!+fi.error.email:notempty=`<span class=\"error\">[[!+fi.error.email]]</span>`]]\n                </label>\n                <input type=\"text\" name=\"email\" id=\"email\" value=\"[[!+fi.email]]\" />\n            </div>\n            <div class=\"form-item\">\n                <label for=\"text\">\n                    Message:\n                    [[!+fi.error.text:notempty=`<span class=\"error\">[[!+fi.error.text]]</span>`]]\n                </label>\n                <textarea name=\"text\" id=\"text\" value=\"[[!+fi.text]]\">[[!+fi.text]]</textarea>\n            </div>\n            <div class=\"form-item\">\n                [[!recaptchav2_render]]\n                [[!+fi.error.recaptchav2_error]]\n            </div>\n            <div class=\"form-button\">\n                <input type=\"submit\" value=\"Send\" class=\"button\" />\n            </div>\n        </form>\n    </div>', 0, 'a:0:{}', 0, ''),
(32, 0, 0, 'sample_formit_contact_eml', '', 0, 6, 0, 'Contact from the website:<br />\n[[+name]] ([[+email]]) wrote: <br />\n \n[[+text]]', 0, 'a:0:{}', 0, ''),
(33, 0, 0, 'recaptchav3_html', '', 0, 6, 0, '<script src=\"https://www.google.com/recaptcha/api.js?render=[[+site_key]]&hl=[[++cultureKey]]\"></script>\n<input type=\"hidden\" name=\"[[+token_key]]\">\n<input type=\"hidden\" name=\"[[+action_key]]\" value=\"[[+form_id]]\">\n<script>\n    grecaptcha.ready(function() {\n        grecaptcha.execute(\'[[+site_key]]\', {action: \'[[+form_id]]\'}).then(function(token) {\n            document.querySelector(\'[name=\"[[+token_key]]\"]\').value = token;\n        });\n    });\n</script>\n', 0, 'a:0:{}', 0, ''),
(35, 1, 0, 'toaster', '', 0, 0, 0, '<div class=\"toast fade hide\" style=\"top: 100px; right: 5%; width: 300px; position: absolute;\" id=\"toastBasic\" role=\"alert\" aria-live=\"assertive\" aria-atomic=\"true\" data-delay=\"3000\">\n    <div id=\"toast-header\" class=\"toast-header\" style=\"background-color: rgba(180, 232, 210, 0.9);\">\n        <i class=\"fas fa-info-circle\" style=\"margin-right: 5px;\"></i>\n        <strong class=\"mr-auto\">[[+header]]</strong>\n        <button class=\"ml-2 mb-1 close\" type=\"button\" data-dismiss=\"toast\" aria-label=\"Close\">\n        <span aria-hidden=\"true\">×</span></button>\n    </div>\n    <div class=\"toast-body\">[[+data]]</div>\n</div>\n                                                            ', 0, 'a:0:{}', 0, ''),
