(38, 1, 0, 'tpl.InsertParam', '', 0, 0, 0, '<div class=\"container\">\n    <div class=\"card mt-3\">\n        <div class=\"card-body\">   \n            <form id=\'insertParam\'>\n                <!-- Form Group (Sensor)-->\n                <div class=\'row\'>\n                <div class=\"form-group col-sm\">\n                    <input type=\'hidden\' id=\'sensor_id\' name=\'sensor_id\' datatype=\'number\' value=\'[[+sensor_id]]\'></input>\n                    <label class=\"mb-1\" for=\"param_name\">Название параметра</label>\n                    <input class=\"form-control form-control-sm\" id=\"param_name\" name=\"param_name\" type=\"text\" datatype=\"string\" placeholder=\"Введите название параметра\"></input>\n                </div>\n                <div class=\"form-group col-sm\">\n                    <label class=\"mb-1\" for=\"param_caption\">Заголовок параметра</label>\n                    <input class=\"form-control form-control-sm\" id=\"param_caption\" placeholder=\"Введите заголовок параметра\"></input>\n                </div>\n                <div class=\"form-group col-sm\">\n                    <label class=\"mb-1\" for=\"param_cmd\">Команда сервера</label>\n                    <input class=\"form-control form-control-sm\" id=\"param_cmd\" placeholder=\"Введите команду сервера\"></input>\n                </div>\n                </div>\n                <div class=\'row\'>\n                <div class=\"form-group col-sm\">\n                    <label class=\"mb-1\" for=\"param_dir\">Направление I/O</label>\n                    <input class=\"form-control form-control-sm\" id=\"param_dir\" placeholder=\"Введите направление (IN/OUT)\"></input>\n                </div>\n                <div class=\"form-group col-sm\">\n                    <label class=\"mb-1\" for=\"param_value\">Значение параметра</label>\n                    <input class=\"form-control form-control-sm\" id=\"param_value\" placeholder=\"Введите значение параметра\"></input>\n          