(39, 1, 0, 'tpl.ParamRaw', '', 0, 0, 0, '<tr id=\"row[[+param_id]]\" class=\"table-data\">\n    <td align=\"right\" class=\"align-middle\"><a href=\'pvalues.html?param_id=[[+param_id]]\'>[[+param_id]]</a></td>\n    <td class=\"edit param_name [[+param_id]] align-middle\">[[+param_name]]</td>\n    <td class=\"edit param_caption [[+param_id]] align-middle\">[[+param_caption]]</td>\n    <td class=\"edit param_cmd [[+param_id]] align-middle\">[[+param_cmd]]</td>\n    <td class=\"edit param_dir [[+param_id]] align-middle\">[[+param_dir]]</td>\n    <td class=\"edit param_value [[+param_id]] align-middle\">[[+param_value]]</td>\n    <td class=\"edit param_suffix [[+param_id]] align-middle\">[[+param_suffix]]</td>\n    <!--<td class=\"edit param_min [[+param_id]] align-middle\">[[+param_min]]</td>\n    <td class=\"edit param_max [[+param_id]] align-middle\">[[+param_max]]</td>\n    <td class=\"edit param_type [[+param_id]] align-middle\">[[+param_type]]</td>\n    <td class=\"edit param_alert_min [[+param_id]] align-middle\">[[+param_alert_min]]</td>\n    <td class=\"edit param_alert_max [[+param_id]] align-middle\">[[+param_alert_max]]</td>\n    <td class=\"edit param_alert_value [[+param_id]] align-middle\">[[+param_alert_value]]</td>-->\n    <td class=\"[[+param_id]]\">\n        <div class=\"custom-control custom-switch\">\n            <input class=\"paramedit [[+param_id]] custom-control-input\" \n            data-flag=\"param_visible\" \n            data-source=\"sensor_params\"\n            data-key=\"param_id\"\n            id=\"visibleToggleSwitch[[+param_id]]\" type=\"checkbox\"  [[+p_visible]]>\n            <label class=\"custom-control-label\" for=\"visibleToggleSwitch[[+param_id]]\"></label>\n        </div>\n    </td>\n        <td>\n            <a class=\"dropdown-item paramdelete [[+param_id]]\" data-table=\"sensor_params\" data-key=\"param_id\" data-value=\"[[+param_id]]\" href=\"javascript:void(0);\">\n            <div class=\"dropdown-item-icon\">\n          